import React from 'react';
import PropTypes from 'prop-types';

import './card.css';

const Card = (props) => {
    const { children, row, wrap, justifyContent, alignItems, border} = props;

    const styles = {
        flexDirection: row ? 'row':'column',
        flexWrap: wrap ? 'wrap':'nowrap',
        justifyContent: justifyContent,
        alignItems: alignItems
    }

    return (
        <div className={`card ${border?'card-bordered':null}`} style={styles}>{children}</div>
    )
}

Card.propTypes = {
    children : PropTypes.any,
    row: PropTypes.bool,
    wrap: PropTypes.bool,
    justifyContent: PropTypes.string,
    alignItems: PropTypes.string,
}

Card.defaultProps ={
    row: false,
    wrap: false,
    justifyContent: 'center',
    alignItems: 'center',
}



export default Card;
