import React from 'react';
import PropTypes from 'prop-types';

import { Button, Card, Text, Image } from '../../atoms';
import './listCard.css';

const ListCard = ({ from, onRemove, ...props }) => {
    const { id, name, owned, nickname } = props;
    const removeButton = onRemove ? <Button btnIcon='trash' onClick={() => onRemove(id)}></Button> : null;
    const ownedText = owned || owned === 0 ? (<Text small>{`Owned: ${owned}`}</Text>) : null;
    const nickNameText = nickname ? (<Text small>{`Nick Name: ${nickname}`}</Text>) : null;
    return (
        <Card wrap fullItem row style={{marginBottom:20}}>
            <div className="list-item-remove">
                {removeButton}
            </div>
            <div className="list-item-left">
                <Image value={id} type="list" alt={`imageList${id}`}/>
                <div>
                    <Text header>{name}</Text>
                    {ownedText}
                    {nickNameText}
                </div>
            </div>
            <div className="list-item-right">
                <Button to={`/pokemon/${from}/${id}`} >Details</Button>
            </div>
        </Card>
    )
}

ListCard.propTypes = {
    id: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
    owned: PropTypes.number,
    nicName: PropTypes.string,
    onRemove: PropTypes.func,
    from: PropTypes.string.isRequired
}

export default ListCard;
