import React from 'react';
import { Card, CardContent, CardItem, CardHeader, Loader, Text } from '../../atoms';
import { ImagePreview, BasicProfile, BasicInfo, Abilities, StatsList } from '../../molekules';
import { CatchPokemon } from '../../../containers';

import "./pokemonCard.css";

const ProfileCard = ({isLoading, failed, detailsFetched, useCatch, ...props}) => {
    const { name, owned, types, sprites, height, weight, abilities, stats, nickname } = props;
    return (
        <div className="profile-container">
            {failed && detailsFetched && <div className="profile-container-error">
                <Text headerMain>Pokemon data is not setup yet. Please, go back to home first.</Text>
            </div>}
            {detailsFetched &&  <>
                <Card border withMargin center>
                    <ImagePreview sprites={sprites} />
                    <BasicProfile name={name}
                        nickname={nickname}
                        owned={owned}
                        types={types}/>
                    {useCatch && <CardItem full><CatchPokemon/></CardItem>}
                </Card>
                <Card border withMargin wrap fullItem>
                    <CardHeader top>Details</CardHeader>
                    <CardContent row>
                        <BasicInfo height={height} weight={weight} />
                        <Abilities abilities={abilities} />
                    </CardContent>
                    <CardHeader>Stats</CardHeader>
                    <StatsList stats={stats} />
                </Card>
            </>}
            <Loader show={isLoading} />
        </div>
    )
}

export default ProfileCard;
