import React, {Component} from 'react';

import { ListItem } from '../../components/organisms';

class MyPokemonList extends Component {
    state = {
        pokemonData: [],
        start: 0,
        offsets: 20,
        loading: false
    }
    componentDidMount(){
        return this.loadData();        
    }

    removePokemon = (id)=>{
        console.log(id)
    }

    loadData = () =>{
        const { myPokemon } = this.props;
        const {start, offsets, pokemonData} = this.state;

        const newArr = myPokemon.slice(start, offsets)
        if(newArr.length !== 0){
            this.setState({
                pokemonData: this.state.pokemonData.concat(newArr),
                start: newArr.length + pokemonData.length,
                offsets: newArr.length + pokemonData.length + 20
            })  
        }
    }

    render(){
        const { pokemonData,loading } = this.state;

        return (
            <ListItem 
                from="owned"
                failed={false} 
                isLoading={loading}
                list={pokemonData}
                loadMore={() => this.loadData()}
                onRemove={(pokeID) => this.removePokemon(pokeID)}/>
        )
    }

}

export default MyPokemonList;
