import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { PokemonCard } from '../../components/organisms';
import { getPokemonDetails, getPokemonList } from '../../store/actions';
import { connect } from 'react-redux';

class PokemonDetails extends Component {
    componentDidMount() {
        const { id, from } = this.props.match.params
        if(!this.props.isFetched){
            this.props.getPokemonList().then(()=>{
                return this.props.getPokemonDetails(id,from)
            });
        } 
        return this.props.getPokemonDetails(id, from);
    }

    render() {
        const { pokemon, isLoading, detailsFetched, error } = this.props;
        return (
            <PokemonCard 
                isLoading ={isLoading}
                failed ={error}
                isLoaded = {detailsFetched}
                {...pokemon}/>
        )
    }
}

const mapStateToProps = (state) => ({
    pokemon: state.pokemon.selectedPokemon,
    isLoading: state.pokemon.isLoading,
    detailsFetched: state.pokemon.detailsFetched,
    error: state.pokemon.error,
    isFetched: state.pokemon.isFetched
})  

const mapDispatchToProps = {
    getPokemonDetails,
    getPokemonList
}

export default connect(mapStateToProps,mapDispatchToProps)(withRouter(PokemonDetails))
