import React from 'react';
// import { Redirect } from 'react-router-dom';
import { Card, CardContent, CardItem, CardHeader, Button, Loader, Text } from '../../atoms';
import { ImagePreview, BasicProfile, BasicInfo, Abilities, StatsList } from '../../molekules';

import "./pokemonCard.css";

const ProfileCard = ({isLoading, failed, isFetched,...props}) => {
    const { name, owned, types, sprites, height, weight, abilities, stats } = props;
    return (
        <div className="profile-container">
            {failed && !isFetched && <div className="profile-container-error">
                    <Text headerMain>Something went wrong while fetching pokemon data. Please, try again later.</Text>
                    {/* <Redirect to='/'/> */}
                    </div>}
            {!isLoading && isFetched && <>
                <Card border withMargin center>
                    <ImagePreview sprites={sprites} />
                    <BasicProfile name={name}
                        owned={owned}
                        types={types} />
                    <CardItem full>
                        <Button className="btn">Catch Pokemon</Button>
                    </CardItem>
                </Card>
                <Card border withMargin wrap fullItem>
                    <CardHeader top>Details</CardHeader>
                    <CardContent row>
                        <BasicInfo height={height} weight={weight} />
                        <Abilities abilities={abilities} />
                    </CardContent>
                    <CardHeader>Stats</CardHeader>
                    <StatsList stats={stats} />
                </Card>
            </>
            }
            <Loader show={isLoading} />
        </div>
    )
}

export default ProfileCard;
