import React from 'react';
import PropTypes from 'prop-types';

import { Button, Card } from '../../atoms';
import './listCard.css';

const ListCard = ({ from, onRemove, ...props }) => {
    const { id, name, owned, nickname } = props;
    const removeButton = onRemove ? <Button btnIcon='trash' onClick={() => onRemove(id)}></Button> : null;
    const ownedText = owned || owned === 0 ? (<small>{`Owned: ${owned}`}</small>) : null;
    const nickNameText = nickname ? (<small>{`Nick Name: ${nickname}`}</small>) : null;
    return (
        <Card wrap fullItem row>
            <div className="list-item-remove">
                {removeButton}
            </div>
            <div className="list-item-left">
                <div className="list-item-image">
                    <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id}.png`} alt="Logo" />
                </div>
                <div className="list-item-info">
                    <p>{name}</p>
                    {ownedText}
                    {nickNameText}
                </div>
            </div>
            <div className="list-item-right">
                <Button to={`/pokemon/${from}/${id}`} >Details</Button>
            </div>
        </Card>
    )
}

ListCard.propTypes = {
    id: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
    owned: PropTypes.number,
    nicName: PropTypes.string,
    onRemove: PropTypes.func,
    from: PropTypes.string.isRequired
}

export default ListCard;
