import React from 'react';
import PropTypes from 'prop-types';

import { ListCard } from '../../molekules';
import { Loader, Button } from '../../atom';

import './listItem.css';

const ListItem = (props) => {
    const { list, from, isLoading, failed ,loadMore} = props
    return (
        <>
            <div className="list-container">
                {failed && <div className="list-container-error">Something went wrong while fetching poekmon data. Please, try again later.</div>}
                {list.map((pokemon) => <ListCard key={pokemon.id} from={from} {...pokemon} />)}
            </div>
            <Button onClick={() => loadMore()}>Load More</Button>
            <Loader show={true} />
        </>
    )
}

ListItem.propTypes = {
    list: PropTypes.arrayOf(PropTypes.object).isRequired,
    isLoading: PropTypes.bool,
    failed: PropTypes.bool,
    loadMore: PropTypes.func
}

export default ListItem;
