import React from 'react';
import { Card, CardContent, CardItem, Text, Button, Label } from '../../atoms';
import { ImagePreview } from '../../molekules';

const ProfileCard = (props) => {
    const { pokemon,catching } = props;
    return (
        <Card border>
            <ImagePreview sprites={pokemon.sprites}/>
            <CardContent>
                <Text headerMain>{pokemon.name}</Text>
                {pokemon.owned || pokemon.owned === 0 ? <Text>Owned : {pokemon.owned}</Text> : null}
                <Text withSpacing>Type : </Text>
                <CardItem row>
                    {pokemon.types.map((item, idx) => (<Label key={idx}>{item.type.name}</Label>))}
                </CardItem>
            </CardContent>
            <CardItem>
                <Button className="btn" onClick={() => catching({id:pokemon.id,name:pokemon.name})}>Catch Pokemon</Button>
            </CardItem>
        </Card>
    )
}

export default ProfileCard;
