import React from 'react';
import { Button, Text, CardItem } from '../../atoms';
import { Modal } from '../../molekules';

const BtnDelete = ({ onClick, onSubmit, showModal, onClose, pokeIdx, pekeId })=>{
    return (
        <>
            <Button btnIcon='trash' onClick={() => onClick(pokeIdx,pekeId)}/>
            <Modal onClose={()=>{onClose()}} withClose showModal={showModal}>
                <CardItem>
                    <Text header italic> Are you sure to delete pokemon?</Text>
                    <Button onClick={() => onSubmit()}>Confirm</Button>
                </CardItem>
            </Modal>
        </>
    )
}

export default BtnDelete;
