import React from 'react';
import { connect } from 'react-redux';
import { setToDeleteIndex, deletePokemon, resetToDeleteIndex } from '../../store/actions'
import { BtnDelete } from '../../components/organisms';

const RemovePokemon = (props) => <BtnDelete {...props} />

const mapStateToProps = (state)=>({
    toDeleteIdx: state.pokemon.toDeleteIdx,
    showModal: state.pokemon.toDeleteIdx ? true: false
})  

const mapDispatchToProps = (dispatch, { pokeIdx, pokeId }) => ({
    onClick: () => dispatch(setToDeleteIndex({...pokeIdx})),
    onSubmit: () => dispatch(deletePokemon({...pokeId})),
    onClose: () => dispatch(resetToDeleteIndex())
})

export default connect(mapStateToProps,mapDispatchToProps)(RemovePokemon);
