import PropTypes from 'prop-types';
import classNames from 'classnames';

import './card.css';

const Card = (props) => {
    const { children, row, wrap, border, fullItem, style} = props;

    const cardClass = classNames('card', {
        'card-border': border,
        'card-row': row,
        'card-wrap': wrap,
        'card-full-width': fullItem,
        'card-margin': withMargin
    })

    return (
        <div className={cardClass} style={style}>{children}</div>
    )
}

Card.propTypes = {
    children : PropTypes.any,
    inline: PropTypes.bool,
    row: PropTypes.bool,
    wrap: PropTypes.bool,
    justifyContent: PropTypes.string,
    alignItems: PropTypes.string,
    style: PropTypes.array
}

Card.defaultProps ={
    inline: false,
    row: false,
    wrap: false,
    justifyContent: 'center',
    alignItems: 'center',
}



export default Card;
