import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { PokemonCard } from '../../components/organisms'

class PokemonDetails extends Component {
    componentDidMount() {
        if(!this.props.isFetched){
            this.props.getPokemonList().then(()=>{
                return this.props.getPokemonDetails(this.props.match.params.id)
            });
        } 
        return this.props.getPokemonDetails(this.props.match.params.id);
    }

    render() {
        const { pokemon, isLoading, detailsFetched, error } = this.props;
        return (
            <PokemonCard 
                isLoading ={isLoading}
                failed ={error}
                isFetched = {detailsFetched}
                {...pokemon}/>
        )
    }
}

export default withRouter(PokemonDetails);
