import React from 'react';
import PropTypes from 'prop-types';

import './card.css';

const Card = (props) => {
    const { children, inline, row, wrap, justifyContent, alignItems, border, style} = props;

    const styles = {
        display: inline? 'inline-flex':'flex',
        flexDirection: row ? 'row':'column',
        flexWrap: wrap ? 'wrap':'nowrap',
        justifyContent: justifyContent,
        alignItems: alignItems
    }

    return (
        <div className={`card ${border?'card-bordered':''}`} style={styles,style}>{children}</div>
    )
}

Card.propTypes = {
    children : PropTypes.any,
    inline: PropTypes.bool,
    row: PropTypes.bool,
    wrap: PropTypes.bool,
    justifyContent: PropTypes.string,
    alignItems: PropTypes.string,
}

Card.defaultProps ={
    inline: false,
    row: false,
    wrap: false,
    justifyContent: 'center',
    alignItems: 'center',
}



export default Card;
