import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';

import './button.css';

const Button = (props) => {
    const { to, disabled, onClick, btnRemove, children } = props;
    if (to) {
        return <Link className="btn" to={to}>{children}</Link>   
    }
    return <button className={`btn ${xxs?'btn-remove':null}`} onClick={onClick} disabled={disabled}>{children}</button> 
}

Button.propTypes = {
    children : PropTypes.string,
    onClick: PropTypes.func,
    to: PropTypes.string,
    disabled: PropTypes.bool,
    btnRemove: PropTypes.bool,
}

Button.defaultProps ={
    onClick: ()=>{},
    disabled: false
}

export default Button;
