import { getPokemonDetails, catchPokemon, resetCatchFailure } from '../../store/actions'
import { connect } from 'react-redux';
import PokemonDetails from './pokemonDetails'


function mapStateToProps(state){
    return{
      pokemon: state.pokemon
    }
}  
const mapDispatchToProps = {
    getPokemonDetails,
    catchPokemon,
    resetCatchFailure
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(PokemonDetails)
