import React from 'react';
import PropTypes from 'prop-types';

import { Button } from '../../atom';
import './listCard.css';

const ListCard = ({ from, onRemove, ...props}) => {
    const { id, name, owned, nicName } = props;
    const removeButton = onRemove? <Button onClick={(id)=>onRemove(id)}>Remove</Button> : null;
    const ownedText = owned || owned === 0? (<small>{`Owned: ${owned}`}</small>):null;
    const nicNameText = nicName ? (<small>{`Nic Name: ${nicName}`}</small>):null;
    return (
        <div className="list-item">
            <div className="list-item-left">
                <div className="list-item-image">
                    <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id}.png`} alt="Logo" />
                </div>
                <div className="list-item-info">
                    <p>{name}</p>
                    {ownedText}
                    {nicNameText}
                </div>
            </div>
            <div className="list-item-right">
                <Button goTo={`/pokemon/${from}/${id}`}>{`/pokemon/${from}/${id}`}</Button>
                {removeButton}
            </div>
        </div>
    )
}

ListCard.propTypes = {
    id: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
    owned: PropTypes.number,
    nicName: PropTypes.string,
    onRemove: PropTypes.func,
    from: PropTypes.string.isRequired
}

export default ListCard;
